    static let randomTimeIntervals =  (-1728000...3456000)

    static func addUser(total: Int, in context: NSManagedObjectContext) {

        for _ in 0..<total  {
            let user = NSEntityDescription.insertNewObject(forEntityName: "User", into: context) as! User

            user.firstName = UserHelper.randomFirstNames.randomElement()!
            user.lastName = UserHelper.randomLastNames.randomElement()!
            user.dob = Date(timeIntervalSince1970:  TimeInterval(UserHelper.randomTimeIntervals.randomElement()!))
            user.isActive = ((UserHelper.randomTimeIntervals.randomElement()! % 2) != 0)
        }

    }



